import React, { useState, useEffect } from "react";
import End from "./End";
import axios from "axios";

export default function Start(props) {
  const [data, setData] = useState([{}]); //전체 데이터
  const [test, setTest] = useState(""); //문항
  const [page, setPage] = useState(0); //페이지
  const [response, setResponse] = useState([]);
  const [pageQuestions, setPageQuestions] = useState([]);

  function fetchData() {
    axios
      .get(
        "http://www.career.go.kr/inspct/openapi/test/questions?apikey=5894ecf675f64f28cd358b417ed6ad1b&q=6"
      )
      .then((response) => {
        const data = response.data.RESULT;
        setData(data);
      })
      .catch((Error) => {
        console.log(Error);
      });
  }
  useEffect(() => {
    fetchData();
  }, []);

  useEffect(() => {
    console.log(data.slice(page * 5, page * 5 + 5));
    setPageQuestions(data.slice(page * 5, page * 5 + 5));
  }, [page]);

  const Questions = ({ questions }) => {
    return questions.map((question) => {
      <div>
        <label>
          <input
            type="radio"
            name={question.qitemNo}
            value={question.answer01}
          />
          {question.answer01}
        </label>
        <label>
          <input
            type="radio"
            name={question.qitemNo}
            value={question.answer02}
          />
          {question.answer02}
        </label>
      </div>;
    });
  };

  //버튼 누르면 다음문항으로 이동
  const changePage = () => {
    console.log("다음문항으로이동");
  };
  //페이지 증가
  const onChangePage = () => {
    setPage(page + 1);
  };

  return (
    <>
      <div>
        <Questions questions={pageQuestions} />
      </div>
      <button onClick={onChangePage}>다음</button>
    </>
  );
}

