import React, { useState, useEffect } from "react";
import End from "./End";
import axios from "axios";
import { Container, Box, Button, Grid } from "@material-ui/core";

export default function Start(props) {
  const [data, setData] = useState([{}]); //전체 데이터
  const [test, setTest] = useState(""); //문항
  const [page, setPage] = useState(0); //페이지
  const [response, setResponse] = useState([]);
  const [pageQuestions, setPageQuestions] = useState([]);

  function fetchData() {
    axios
      .get(
        "http://www.career.go.kr/inspct/openapi/test/questions?apikey=5894ecf675f64f28cd358b417ed6ad1b&q=6"
      )
      .then((response) => {
        const data = response.data.RESULT;
        setData(data);
      })
      .catch((Error) => {
        console.log(Error);
      });
  }
  useEffect(() => {
    fetchData();
  }, []);

  useEffect(() => {
    setPageQuestions(data.slice(page * 4, page * 4 + 4));
  }, [page, data]);

  const Questions = ({ questions }) => {
    const [radioState, setRadioState] = useState(["", "", "", ""]);
    const [buttonActive, setButtonActive] = useState(true);

    useEffect(() => {
      checkAllChecked();
      console.log(radioState);
    }, [radioState]);

    const onChangeRadio = (e, idx) => {
      const prevArray = radioState;
      const newArray = [
        ...prevArray.slice(0, idx),
        e.target.value,
        ...prevArray.slice(idx + 1, prevArray.lenght),
      ];
      setRadioState(newArray);
    };
    const checkAllChecked = () => {
      setButtonActive(radioState.indexOf("") == -1 ? false : true);
    };

    console.log(questions);
    return (
      <Box>
        {questions.map((question, idx) => {
          return (
            <div>
              <label>
                <input
                  type="radio"
                  name={question.qitemNo}
                  value={question.answerScore01}
                  onChange={(e) => {
                    onChangeRadio(e, idx);
                  }}
                />
                {question.answer01}
              </label>
              <label>
                <input
                  type="radio"
                  name={question.qitemNo}
                  value={question.answerScore02}
                  onChange={(e) => {
                    onChangeRadio(e, idx);
                  }}
                />
                {question.answer02}
              </label>
            </div>
          );
        })}
        {page !== 0 && (
          <Button variant="contained" color="primary" onClick={onPrevPage}>
            이전
          </Button>
        )}
        <Button
          variant="contained"
          color="primary"
          disabled={buttonActive}
          onClick={onChangePage}
        >
          {page == 6 ? "제출" : "다음"}
        </Button>
      </Box>
    );
  };

  //버튼 누르면 다음문항으로 이동
  const changePage = () => {
    console.log("다음문항으로이동");
  };
  //페이지 증가
  const onChangePage = () => {
    if (page == 6) {
      props.history.push("/end");
    } else {
      setPage(page + 1);
    }
  };

  const onPrevPage = () => {
    setPage(page - 1);
  };

  return (
    <>
      <Grid>
        <Grid container spacing={3}>
          <Grid item xs={12}>
            <Paper className={classes.paper}>xs=12</Paper>
          </Grid>
          <Grid item xs={6}>
            <Paper className={classes.paper}>xs=6</Paper>
          </Grid>
          <Grid item xs={6}>
            <Paper className={classes.paper}>xs=6</Paper>
          </Grid>
          <Grid item xs={3}>
            <Paper className={classes.paper}>xs=3</Paper>
          </Grid>
          <Grid item xs={3}>
            <Paper className={classes.paper}>xs=3</Paper>
          </Grid>
          <Grid item xs={3}>
            <Paper className={classes.paper}>xs=3</Paper>
          </Grid>
          <Grid item xs={3}>
            <Paper className={classes.paper}>xs=3</Paper>
          </Grid>
        </Grid>
        <Questions questions={pageQuestions} />
      </Grid>
    </>
  );
}

