import React, { useState, useEffect } from "react";
import axios from "axios";
import User from "./User.js";
import {
  Container,
  Box,
  Button,
  Grid,
  RadioGroup,
  FormControlLabel,
  FormControl,
  FormLabel,
  Radio,
} from "@material-ui/core";
// import "./styles.css";
import {
  Radar,
  RadarChart,
  PolarGrid,
  PolarAngleAxis,
  PolarRadiusAxis,
} from "recharts";
import { makeStyles } from "@material-ui/core/styles";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import Link from "@material-ui/core/Link";

export default function Result(props) {
  const [graphdata, setGraphdata] = useState([]); //전체 데이터
  const [name, setName] = useState([{}]); //이름
  const [gender, setGender] = useState([{}]); //성별
  const [date, setDate] = useState([{}]); //검사일
  const [score, setScore] = useState([]); //결과
  const seq = props.location.state.url.split("=")[1];
  const url = `https://inspct.career.go.kr/inspct/api/psycho/report?seq=${seq}`;
  const [major, setMajor] = useState(null);
  const [edulevel, setEdulevel] = useState([[], [], [], [], []]); //학력별
  const [realgraphdata, setRealgraphdata] = useState([]);
  const [edutable, setEdutable] = useState([]);
  const [majortable, setMajortable] = useState({}, {}, {}, {}, {}, {}, {}, {});
  const item = [
    "능력발휘",
    "자율성",
    "보수",
    "안정성",
    "사회적 안정",
    "사회봉사",
    "자기계발",
    "창의성",
  ];
  const edulevellist = ["중졸", "고졸", "전문대졸", "대졸", "대학원졸"];
  const majorlist = [
    "계열무관",
    "인문",
    "사회",
    "교육",
    "공학",
    "자연",
    "의학",
    "예능",
  ];
  const useStyles = makeStyles({
    table: {
      minWidth: 650,
    },
  });
  const classes = useStyles();

  //결과 요청
  async function fetchResult() {
    const response = await axios.get(url);

    setName(response.data.user.name);
    setGender(response.data.user.grade);
    setDate(response.data.inspct.beginDtm.slice(0, 10));
    setScore(response.data.result.wonScore.split(" "));
  }
  useEffect(() => {
    fetchResult();
  }, []);

  useEffect(() => {
    let scoreArr = [];

    score.map((sc, idx) => {
      if (sc != "") {
        scoreArr.push({ item: item[idx], score: sc.split("=")[1] });
      }
    });
    scoreArr.splice(8, 1);
    setGraphdata(scoreArr); //마지막 undefine 삭제
  }, [score]);
  // console.log(graphdata);

  useEffect(() => {
    let temp = [];
    if (graphdata != " " && graphdata != null) {
      graphdata.map((data, idx) => {
        temp.push({
          subject: data.item,
          A: data.score,
          B: 110,
          fullMark: 150,
        });
      });
      setRealgraphdata(temp);
    }
  }, [graphdata]);
  console.log(realgraphdata);

  const onChangePage = () => {
    props.history.push("/");
  };

  //학력별 직업 요청
  async function fetchEduInfo(top1, top2) {
    let temp = [[], [], [], [], []];
    const education = await axios.get(
      `https://inspct.career.go.kr/inspct/api/psycho/value/jobs?no1=${top1}&no2=${top2}`
    );
    education.data.map((edu, idx) => {
      temp[edu[2] - 1].push({ id: edu[0], job: edu[1] });
      // console.log(edu[2]);
    });
    setEdulevel(temp);
    // console.log(temp);
    // console.log(education.data);
  }
  async function fetchMajorInfo(top1, top2) {
    let temp = [[], [], [], [], [], [], [], []];
    console.log(top1, top2);
    const majorInfo = await axios.get(
      `https://inspct.career.go.kr/inspct/api/psycho/value/majors?no1=${top1}&no2=${top2}`
    );
    console.log(majorInfo);
    majorInfo.data.map((major, idx) => {
      temp[major[2]].push({ id: major[0], major: major[1] });
    });
    setMajor(temp);
    console.log(temp);
  }

  useEffect(() => {
    if (graphdata[0]) {
      let temp = graphdata;
      temp.sort(function (a, b) {
        return b["score"] - a["score"];
      });
      console.log(temp);
      console.log(temp[0]);
      fetchEduInfo(
        item.indexOf(temp[0].item) + 1,
        item.indexOf(temp[1].item) + 1
      );
      fetchMajorInfo(
        item.indexOf(temp[0].item) + 1,
        item.indexOf(temp[1].item) + 1
      );
      console.log(
        item.indexOf(temp[0].item) + 1,
        item.indexOf(temp[1].item) + 1
      );
    }
  }, [graphdata]);
  console.log("this is major");
  console.log(major);
  console.log("this is edulevel");
  console.log(edulevel);

  function createData(edulevel) {
    let temp = [];
    console.log("temp:", temp);
    edulevel.map((edu, idx) => {
      var tmpjob = "";
      edu.map((ed, idx2) => {
        console.log("ed:", ed);
        var html =
          "<Link href='https://www.career.go.kr/cnet/front/base/job/jobView.do?SEQ=" +
          ed.id +
          "'>" +
          ed.job +
          "</Link>";
        // tmpjob = tmpjob + " " + ed.job;

        tmpjob = tmpjob + " " + html;
        console.log("temp0: idx: " + idx, tmpjob);
      });
      temp.push({ id: edulevellist[idx], jobs: tmpjob });
    });
    console.log("temp:", temp);
    setEdutable(temp);

    // edulevel.map((edu, idx) => {
    //   // console.log("edu:", edu);
    //   edu.map((ed, idx2) => {
    //     console.log("ed:", ed);
    //     temp.push({
    //       item: edulevellist[idx],
    //       jobinfo: { id: ed.id, job: ed.job },
    //     });
    //     console.log("temp0:", temp);
    //   });
    // });

    // return { id, jobs };
  }
  useEffect(() => {
    if (edulevel[1]) {
      createData(edulevel);
    }
  }, [edulevel]);

  return (
    <Grid container justify="center">
      <h1>직업 가치관 검사 결과표</h1>
      <Grid container justify="center">
        <p>이름:{JSON.stringify(name)}</p>
        <p>성별:{JSON.stringify(gender)}</p>
        <p>날짜:{JSON.stringify(date)}</p>
      </Grid>
      <Grid container justify="center">
        <RadarChart
          cx="50%"
          cy="50%"
          outerRadius={150}
          width={500}
          height={500}
          data={realgraphdata}
        >
          <PolarGrid />
          <PolarAngleAxis dataKey="subject" />
          <PolarRadiusAxis />
          <Radar
            name="Mike"
            dataKey="A"
            stroke="#8884d8"
            fill="#8884d8"
            fillOpacity={0.6}
            s
          />
        </RadarChart>
      </Grid>
      <p>가치관과 관련이 높은 직업</p>
      <br /> <br />
      <p>종사자 평균 학력별</p>
      <TableContainer component={Paper}>
        <Table className={classes.table} aria-label="simple table">
          <TableHead>
            <TableRow>
              <TableCell>분야</TableCell>
              <TableCell align="">직업</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {edutable.map((row) => (
              <TableRow align="left" key={row.id}>
                <TableCell component="th" scope="row">
                  {row.id}
                </TableCell>
                <TableCell align="left">{row.jobs}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
      {/* <p>{JSON.stringify(edulevel)}</p> */}
      <p>종사자 평균 전공별</p>
      <p>{JSON.stringify(major)}</p>
      <Grid container justify="center">
        <Button variant="contained" color="primary" onClick={onChangePage}>
          다시 검사하기
        </Button>
      </Grid>
    </Grid>
  );
}
