import React, { useState, useEffect } from "react";
import axios from "axios";
import User from "./User.js";
import {
  Container,
  Box,
  Button,
  Grid,
  RadioGroup,
  FormControlLabel,
  FormControl,
  FormLabel,
  Radio,
} from "@material-ui/core";
// import "./styles.css";
import {
  Radar,
  RadarChart,
  PolarGrid,
  PolarAngleAxis,
  PolarRadiusAxis,
} from "recharts";

export default function Result(props) {
  const [alldata, setAlldata] = useState([{}]); //전체 데이터
  const [name, setName] = useState([{}]); //이름
  const [gender, setGender] = useState([{}]); //성별
  const [date, setDate] = useState([{}]); //검사일
  const [score, setScore] = useState([{}]); //결과
  const seq = props.location.state.url.split("=")[1];
  const url = `https://inspct.career.go.kr/inspct/api/psycho/report?seq=${seq}`;
  const [majors, setMajors] = useState(null);
  const [jobs, setJobs] = useState(null);

  const result_list = [
    "능력발휘",
    "자율성",
    "보수",
    "안정성",
    "사회적 안정",
    "사회봉사",
    "자기계발",
    "창의성",
  ];

  let test = JSON.stringify(score).split(" ");
  var array = [{}];
  let array2 = [];

  test.map((sc, idx) => {
    // array[result_list[idx]] = sc.split("=")[1];
    // array2[idx] = sc.split("=")[1];
    array[idx] = { name: result_list[idx], score: sc.split("=")[1] };
    array2[idx] = sc.split("=")[1];
  });
  console.log(array);
  // console.log(array["score"]);

  //top 2개 추출
  var sortingField = "score";
  array.sort(function (a, b) {
    return b[sortingField] - a[sortingField];
  });

  //   <p>
  //   {JSON.stringify(name)}, {JSON.stringify(gender)},{" "}
  //   {JSON.stringify(date)}
  // </p>
  function getJobs(rank1, rank2) {
    const job_school = `https://inspct.career.go.kr/inspct/api/psycho/value/jobs?no1=${rank1}&no2=${rank2}`;
    const job_major = `https://inspct.career.go.kr/inspct/api/psycho/value/majors?no1=${rank1}&no2=${rank2}`;

    axios.get(job_school).then((response) => {
      console.log("10-1. job_school:", response.data);

      let school = [];

      for (let i = 0; i < 6; i++) {
        school.push(
          response.data.filter((x) => {
            return x[2] === i;
          })
        );
      }

      console.log("11-1. school:", school);
      setSchool(school);
    });

    axios.get(job_major).then((response) => {
      console.log("10-2. job_major:", response.data);

      let major = [];

      for (let i = 0; i < 8; i++) {
        major.push(
          response.data.filter((x) => {
            return x[2] === i;
          })
        );
      }

      console.log("11-2. major:", major);
      setMajor(major);
    });
  }

  const data = [
    {
      subject: "능력발휘",
      A: array2[0],
      B: 110,
      fullMark: 150,
    },
    {
      subject: "자율성",
      A: array2[1],
      B: 110,
      fullMark: 150,
    },
    {
      subject: "보수",
      A: array2[2],
      B: 110,
      fullMark: 150,
    },
    {
      subject: "안정성",
      A: array2[3],
      B: 130,
      fullMark: 150,
    },
    {
      subject: "사회적 인정",
      A: array2[4],
      B: 130,
      fullMark: 150,
    },
    {
      subject: "사회봉사",
      A: array2[5],
      B: 100,
      fullMark: 150,
    },
    {
      subject: "자기계발",
      A: array2[6],
      B: 90,
      fullMark: 150,
    },
    {
      subject: "창의성",
      A: array2[7],
      B: 85,
      fullMark: 150,
    },
  ];
  // const data = [
  //   {
  //     subject: "능력발휘",
  //     A: array["능력발휘"],
  //     B: 110,
  //     fullMark: 150,
  //   },
  //   {
  //     subject: "자율성",
  //     A: array["자율성"],
  //     B: 110,
  //     fullMark: 150,
  //   },
  //   {
  //     subject: "보수",
  //     A: array["보수"],
  //     B: 110,
  //     fullMark: 150,
  //   },
  //   {
  //     subject: "안정성",
  //     A: array["안정성"],
  //     B: 130,
  //     fullMark: 150,
  //   },
  //   {
  //     subject: "사회적 인정",
  //     A: array[4],
  //     B: 130,
  //     fullMark: 150,
  //   },
  //   {
  //     subject: "사회봉사",
  //     A: array["사회봉사"],
  //     B: 100,
  //     fullMark: 150,
  //   },
  //   {
  //     subject: "자기계발",
  //     A: array["자기개발"],
  //     B: 90,
  //     fullMark: 150,
  //   },
  //   {
  //     subject: "창의성",
  //     A: array["창의성"],
  //     B: 85,
  //     fullMark: 150,
  //   },
  // ];

  async function fetchResult() {
    const response = await axios.get(url);

    setName(response.data.user.name);
    setGender(response.data.user.grade);
    setDate(response.data.inspct.beginDtm.slice(0, 10));
    setScore(response.data.result.wonScore);
  }
  useEffect(() => {
    fetchResult();
  }, []);
  const onChangePage = () => {
    props.history.push("/");
  };

  function fetchjob() {}

  return (
    <Grid container justify="center">
      <h1>직업 가치관 검사 결과표</h1>
      <Grid container justify="center">
        <RadarChart
          cx={300}
          cy={250}
          outerRadius={150}
          width={500}
          height={500}
          data={data}
        >
          <PolarGrid />
          <PolarAngleAxis dataKey="subject" />
          <PolarRadiusAxis />
          <Radar
            name="Mike"
            dataKey="A"
            stroke="#8884d8"
            fill="#8884d8"
            fillOpacity={0.6}
            s
          />
        </RadarChart>
      </Grid>
      <p>가치관과 관련이 높은 직업</p>
      <br /> <br />
      <p>종사자 평균 학력별</p>
      <p>종사자 평균 전공별</p>
      <Grid container justify="center">
        <Button variant="contained" color="primary" onClick={onChangePage}>
          다시 검사하기
        </Button>
      </Grid>
    </Grid>
  );
}
