import React, { useState, useEffect } from "react";
import axios from "axios";
import User from "./User.js";
import {
  Container,
  Box,
  Button,
  Grid,
  RadioGroup,
  FormControlLabel,
  FormControl,
  FormLabel,
  Radio,
} from "@material-ui/core";
// import "./styles.css";
import {
  Radar,
  RadarChart,
  PolarGrid,
  PolarAngleAxis,
  PolarRadiusAxis,
} from "recharts";

export default function Result(props) {
  const [alldata, setAlldata] = useState([{}]); //전체 데이터
  const [name, setName] = useState([{}]); //이름
  const [gender, setGender] = useState([{}]); //성별
  const [date, setDate] = useState([{}]); //검사일
  const [score, setScore] = useState([{}]); //결과
  const result_url = props.location.state.url.split("=")[1];
  const url = `https://inspct.career.go.kr/inspct/api/psycho/report?seq=${result_url}`;

  let test = score.split(" ");
  let array = [];
  // console.log(score.split(" "));

  test.map((sc, idx) => {
    array[idx] = sc.split("=")[1];
  });
  // console.log(array);

  const data = [
    {
      subject: "능력발휘",
      A: array[0],
      B: 110,
      fullMark: 150,
    },
    {
      subject: "자율성",
      A: array[1],
      B: 110,
      fullMark: 150,
    },
    {
      subject: "보수",
      A: array[2],
      B: 110,
      fullMark: 150,
    },
    {
      subject: "안정성",
      A: array[3],
      B: 130,
      fullMark: 150,
    },
    {
      subject: "사회적 인정",
      A: array[4],
      B: 130,
      fullMark: 150,
    },
    {
      subject: "사회봉사",
      A: array[5],
      B: 100,
      fullMark: 150,
    },
    {
      subject: "자기계발",
      A: array[6],
      B: 90,
      fullMark: 150,
    },
    {
      subject: "창의성",
      A: array[7],
      B: 85,
      fullMark: 150,
    },
  ];
  async function fetchResult() {
    const response = await axios.get(url);

    setName(response.data.user.name);
    setGender(response.data.user.grade);
    setDate(response.data.inspct.beginDtm.slice(0, 10));
    setScore(response.data.result.wonScore);
  }
  useEffect(() => {
    fetchResult();
  }, []);
  const onChangePage = () => {
    props.history.push("/");
  };

  return (
    <div>
      <h1>직업 가치관 검사 결과표</h1>
      <p>이름: {name}</p>
      <p>성별: {JSON.stringify(gender) == "100323" ? "남성" : "여성"}</p>
      <p>검사일: {JSON.stringify(date)}</p>

      <h2>직업 가치관 결과</h2>
      <RadarChart
        cx={300}
        cy={250}
        outerRadius={150}
        width={500}
        height={500}
        data={data}
      >
        <PolarGrid />
        <PolarAngleAxis dataKey="subject" />
        <PolarRadiusAxis />
        <Radar
          name="Mike"
          dataKey="A"
          stroke="#8884d8"
          fill="#8884d8"
          fillOpacity={0.6}
        />
      </RadarChart>

      <Box style={{ display: "flex", justifyContent: "space-between" }}>
        <Button variant="contained" color="primary" onClick={onChangePage}>
          다시 검사하기
        </Button>
      </Box>
    </div>
  );
}
