import React, { useState, useEffect } from "react";
import axios from "axios";
import User from "./User.js";
import {
  Container,
  Box,
  Button,
  Grid,
  RadioGroup,
  FormControlLabel,
  FormControl,
  FormLabel,
  Radio,
} from "@material-ui/core";
// import "./styles.css";
import {
  Radar,
  RadarChart,
  PolarGrid,
  PolarAngleAxis,
  PolarRadiusAxis,
} from "recharts";
import { makeStyles } from "@material-ui/core/styles";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableContainer from "@material-ui/core/TableContainer";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
export default function Result(props) {
  const [alldata, setAlldata] = useState([{}]); //전체 데이터
  const [name, setName] = useState([{}]); //이름
  const [gender, setGender] = useState([{}]); //성별
  const [date, setDate] = useState([{}]); //검사일
  const [score, setScore] = useState([{}]); //결과
  const result_url = props.location.state.url.split("=")[1];
  const url = `https://inspct.career.go.kr/inspct/api/psycho/report?seq=${result_url}`;
  const useStyles = makeStyles({
    table: {
      minWidth: "100%",
    },
  });
  const classes = useStyles();

  let test = JSON.stringify(score).split(" ");
  let array = [];
  // console.log(score.split(" "));

  test.map((sc, idx) => {
    array[idx] = sc.split("=")[1];
  });
  // console.log(array);

  function createData(name, genders, dates) {
    return { name, genders, dates };
  }
  const rows = [
    createData(
      JSON.stringify(name),
      JSON.stringify(gender),
      JSON.stringify(date)
    ),
  ];
  //   <p>
  //   {JSON.stringify(name)}, {JSON.stringify(gender)},{" "}
  //   {JSON.stringify(date)}
  // </p>
  const data = [
    {
      subject: "능력발휘",
      A: array[0],
      B: 110,
      fullMark: 150,
    },
    {
      subject: "자율성",
      A: array[1],
      B: 110,
      fullMark: 150,
    },
    {
      subject: "보수",
      A: array[2],
      B: 110,
      fullMark: 150,
    },
    {
      subject: "안정성",
      A: array[3],
      B: 130,
      fullMark: 150,
    },
    {
      subject: "사회적 인정",
      A: array[4],
      B: 130,
      fullMark: 150,
    },
    {
      subject: "사회봉사",
      A: array[5],
      B: 100,
      fullMark: 150,
    },
    {
      subject: "자기계발",
      A: array[6],
      B: 90,
      fullMark: 150,
    },
    {
      subject: "창의성",
      A: array[7],
      B: 85,
      fullMark: 150,
    },
  ];
  async function fetchResult() {
    const response = await axios.get(url);

    setName(response.data.user.name);
    setGender(response.data.user.grade);
    setDate(response.data.inspct.beginDtm.slice(0, 10));
    setScore(response.data.result.wonScore);
  }
  useEffect(() => {
    fetchResult();
  }, []);
  const onChangePage = () => {
    props.history.push("/");
  };

  return (
    <Grid container justify="center">
      <h1>직업 가치관 검사 결과표</h1>
      <Grid container justify="center">
        <Grid container justify="center">
          <div>
            <TableContainer component={Paper}>
              <Table className={classes.table} aria-label="simple table">
                <TableHead>
                  <TableRow>
                    <TableCell align="left">이름</TableCell>
                    <TableCell align="right">성별</TableCell>
                    <TableCell align="right">검사일</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {rows.map((row) => (
                    <TableRow key={row.name}>
                      <TableCell align="center">{row.name}</TableCell>
                      <TableCell align="center">{row.genders}</TableCell>
                      <TableCell align="center">{row.dates}</TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
          </div>
        </Grid>
        <RadarChart
          cx={300}
          cy={250}
          outerRadius={150}
          width={500}
          height={500}
          data={data}
        >
          <PolarGrid />
          <PolarAngleAxis dataKey="subject" />
          <PolarRadiusAxis />
          <Radar
            name="Mike"
            dataKey="A"
            stroke="#8884d8"
            fill="#8884d8"
            fillOpacity={0.6}
            s
          />
        </RadarChart>
      </Grid>
      <p>가치관과 관련이 높은 직업</p>
      <br /> <br />
      <p>종사자 평균 학력별</p>
      <p>종사자 평균 전공별</p>
      <Grid container justify="center">
        <Button variant="contained" color="primary" onClick={onChangePage}>
          다시 검사하기
        </Button>
      </Grid>
    </Grid>
  );
}
